import { GoogleLogin } from 'react-google-login';
import React from 'react';
import { userID, checkLogin, setUserID } from './checklogin';
import { useNavigate } from 'react-router';

const clientId = "904816085137-rtpqsfpv201ik04nb6c81vmhterotdqj.apps.googleusercontent.com"

function Login() {
    
    const onSuccess = (res) => {
        console.log("LOGIN SUCCESS! Current user: ", res);
        setUserID(res['tokenId']);
        fetchUserDetails(res['tokenId'])
        navigate("/atl");
    }

    let navigate = useNavigate();

    const onFailure = (res) => {
        console.log("LOGIN FAILED! res: ", res);
    }

    function fetchUserDetails(cred)
    {
        const Http = new XMLHttpRequest();
        const url = 'https://oauth2.googleapis.com/tokeninfo?id_token=' + cred;
        Http.open("GET", url);
        Http.send();
        
        Http.onreadystatechange = (e) => {
            if(Http.readyState === XMLHttpRequest.DONE) {
                console.log(Http.responseText)
                const parsedResponse = JSON.parse(Http.responseText);
                // window.alert('User ' + parsedResponse['given_name'] + " " + parsedResponse['family_name'] + ' just signed in')
            }
        }
    }

    return (
        <div id="SignInButton">
            <GoogleLogin
                clientId={clientId}
                buttonText="Sign in with Google"
                onSuccess={onSuccess}
                onFailure={onFailure}
                cookiePolicy={'single_host_origin'}
                isSignedIn={true}
                background='black'
            />     
        </div>
    )
}

export default Login;
