import { firstName, lastName, emailAdd } from '../components/login.js'
import { userID } from "../components/checklogin";
import { Navigate } from "react-router-dom";
import LogoutButton from "../components/logout";
import { getUser } from '../axiosApi';
import { useNavigate } from 'react-router';
import CounterInput from "react-counter-input";
import React, { useState, useEffect } from 'react';

import '../App.css'

function PlansPage()
{
    useEffect(() => {
        const loadPost = async () => {

            // Till the data is fetch using API 
            // the Loading page will show.
            setLoading(true);

            // Await make wait until that 
            // promise settles and return its result
            const response = await axios.get(
            "https://jsonplaceholder.typicode.com/posts/");

            // After fetching data stored it in posts state.
            setPosts(response.data);

            // Closed the loading page
            setLoading(false);
        }

        // Call the function
        loadPost();
    }, []);
    const [planDays, setplanDays] = useState({});
    let navigate = useNavigate();

    if (userID === "EMPTY") {
        return <Navigate to="/login" />
    }
    else
    {
        let extend_days = 0;

        function handleExtend() {
            const URLtoSend = '/extend/' + extend_days;
            getUser(URLtoSend);

          }

        function displayPlanDays() {
            const URLtoSend = '/plan/days';
            setplanDays(7)
            // getUser(URLtoSend).then((res) => {
            //     setplanDays(res.data.days)
            // });
        }
        function handleCount(days)
        {
            console.log(days)
            extend_days = days;
            console.log(extend_days)
        }

        function handleCancel() 
        {
            navigate(-1);
        }

        return(
            <div className='subPage'>
                <link rel="stylesheet" href="./App.css"></link>
                <h1 className='HeaderTextMain'>EnergyLive 2022</h1>
                <main>
                    <div className='LogoutPossition'>  
                        <LogoutButton />
                    </div>
                    <h1 className='EmailText'>{ emailAdd }</h1>
                </main>
                <div className='PlansContent'>
                    <div className='Place'>
                        <h1 className='HeaderTextPlans'>Future plans</h1>
                        <h2 className='Header2TextPlans'>Hello {firstName} {lastName}. Would you like to extend your plan? Choose a number of days:</h2>
                        <h2 className='Header2TextPlans'>Current plan: {planDays}</h2>
                        <div className='CounterDiv'>
                            <CounterInput
                                min={0}
                                max={9999999}
                                onCountChange={handleCount}
                                btnStyle={{
                                    fontSize: "25px"
                                }}
                                inputStyle={{
                                    fontSize: "25px"
                                }}
                            />
                        </div>
                        <button className='Extend' onClick={handleExtend}>Extend</button>
                        <button className='Cancel' onClick={handleCancel}>Cancel</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default PlansPage;
